mapper.update:
UPDATE CHAT
SET MODIFY_DATE = (SELECT MAX(M_CREATE_DATE)
                   FROM MESSAGE
                   WHERE CHAT_ID = #{chatId}
                   GROUP BY CHAT_ID)
WHERE CHAT_ID = #{chatId}

UPDATE JOIN_CHAT
SET MODIFY_DATE = SYSDATE
WHERE CHAT_ID = #{chatId} AND EMP_ID = #{empId}

mapper.select:
SELECT EMP_ID
FROM JOIN_CHAT
WHERE CHAT_ID = #{chatId}

select chat_id, chat_name
from join_chat
where chat_id = #{chatId} and emp_id = #{empId}

mapper.update:
UPDATE JOIN_CHAT
SET JOIN_DATE = SYSDATE, MODIFY_DATE=SYSDATE, CHAT_STATUS='Y'
WHERE CHAT_ID = #{chatId} AND EMP_ID = #{empId}

mapper.select:
select alarm_yn
from join_chat
where emp_id = #{empId} and chat_id = #{chatId}

mapper.update:
update join_chat
set alarm_yn = #{alarm}
where chat_id = #{chatId} and emp_id = #{empId}

UPDATE JOIN_CHAT
SET CHAT_STATUS='N'
WHERE CHAT_ID = #{chatId} AND EMP_ID = #{empId}

update join_chat
set chat_name = #{chatName}, name_status = 'Y', AUTO_NAME = FN_KO_CHAR(#{chatName})
where chat_id=#{chatId} and emp_id=#{empId}

UPDATE JOIN_CHAT
SET CHAT_NAME = #{chatName}, NAME_STATUS = 'N', AUTO_NAME = FN_KO_CHAR(#{chatName})
WHERE CHAT_ID = #{chatId} AND EMP_ID = #{empId}
